mapper.select:
SELECT A.order_id AS orderId , C.restaurant_name AS restaurantName , A.rating , A.review_text AS reviewText , A.created_at AS createdAt FROM review A JOIN `order` B ON A.order_id = B.order_id JOIN restaurant C ON B.restaurant_id = C.restaurant_id WHERE B.user_id = #{userId}
AND ( DATE_FORMAT(A.created_at, '%Y-%m-%d') >= #{startDate} AND DATE_FORMAT(A.created_at, '%Y-%m-%d') <= #{endDate} )
AND ( DATE_FORMAT(A.created_at, '%Y-%m-%d') >= #{startDate} )
AND ( DATE_FORMAT(A.created_at, '%Y-%m-%d') <= #{endDate} )
ORDER BY A.created_at DESC LIMIT #{size} OFFSET #{startIdx}

SELECT review_pic AS reviewPic
FROM review_pic
WHERE order_id = #{orderId}
